import { expect } from 'chai';
import { CryptoPublicKeyBytes, CryptoSecretKeyBytes, CryptoBytes } from '../src/const.js';
import { cryptoSign, cryptoSignOpen, cryptoSignVerify, cryptoSignSignature, cryptoSignKeypair } from '../src/sign.js';

const TEST_VECTORS = [
  // Test vectors taken from https://github.com/theQRL/go-qrllib/blob/main/wallet/ml_dsa_87/wallet_test.go#L25
  {
    ctx: '5a4f4e44',
    msg: '4d4c2d4453412d38372074657374206d65737361676520666f72207369676e696e67',
    // sha256 digest of f29f58aff0b00de2844f7e20bd9eeaacc379150043beeb328335817512b29fbb7184da84a092f842b2a06d72a24a5d28
    hashedSeed: 'f1e4118b9beb0e69d9dee85610b7bde4606a3f8415afad4883e84ad59fe20607',
    derivedPK:
      '',
    derivedSK:
      '',
    derivedSig:
      '',
  },
  {
    ctx: '5a4f4e44',
    msg: '4d4c2d4453412d38372074657374206d65737361676520666f72207369676e696e67',
    // sha256 digest of a7b1a3005d9e110009c48d45deb43f0a0e31846ed2c5aaefb6d4238040ad4c08794ffe65585c13eb6948c2faf6db90c2
    hashedSeed: '0133968a0085e3ecf0d21e0213e64c37974a0dc1fc74a073049f23a4efec04bf',
    derivedPK:
      '',
    derivedSK:
      '',
    derivedSig:
      '',
  },
];

describe('cryptoSignKeypair', () => {
  for (let i = 0; i < TEST_VECTORS.length; i++) {
    it(`should generate the expected pk and sk ${i}`, () => {
      const vector = TEST_VECTORS[i];
      const pk = new Uint8Array(CryptoPublicKeyBytes);
      const sk = new Uint8Array(CryptoSecretKeyBytes);

      cryptoSignKeypair(Buffer.from(vector.hashedSeed, 'hex'), pk, sk);
      expect(Buffer.from(pk, 'binary').toString('hex')).to.equal(vector.derivedPK);
      expect(Buffer.from(sk, 'binary').toString('hex')).to.equal(vector.derivedSK);
    });
  }
});

describe('cryptoSignKeypair with null seed generates pk/sk', () => {
  it('should generate the expected pk and sk', () => {
    const pk = new Uint8Array(CryptoPublicKeyBytes);
    const sk = new Uint8Array(CryptoSecretKeyBytes);
    cryptoSignKeypair(null, pk, sk);
    expect(pk.length).to.equal(CryptoPublicKeyBytes);
    expect(sk.length).to.equal(CryptoSecretKeyBytes);
  });
});

describe('cryptoSign', () => {
  for (let i = 0; i < TEST_VECTORS.length; i++) {
    it(`should return the expected signature message ${i}`, () => {
      const vector = TEST_VECTORS[i];
      const msg = Buffer.from(vector.msg, 'hex');
      const sk = Buffer.from(vector.derivedSK, 'hex');
      const ctx = Buffer.from(vector.ctx, 'hex');

      const sigMessage = cryptoSign(msg, sk, false, ctx);
      expect(Buffer.from(sigMessage, 'binary').toString('hex')).to.equal(vector.derivedSig + vector.msg);
    });
  }
});

describe('cryptoSignOpen', () => {
  for (let i = 0; i < TEST_VECTORS.length; i++) {
    it(`should return the expected message ${i}`, () => {
      const vector = TEST_VECTORS[i];
      const sigMessage = Buffer.from(vector.derivedSig + vector.msg, 'hex');
      const pk = Buffer.from(vector.derivedPK, 'hex');

      const openedMessage = cryptoSignOpen(sigMessage, pk);
      expect(Buffer.from(openedMessage, 'binary').toString('hex')).to.equal(vector.msg);
    });
  }
});

describe('cryptoSignVerify', () => {
  for (let i = 0; i < TEST_VECTORS.length; i++) {
    it(`should return true ${i}`, () => {
      const vector = TEST_VECTORS[i];
      const sig = Buffer.from(vector.derivedSig, 'hex');
      const msg = Buffer.from(vector.msg, 'hex');
      const pk = Buffer.from(vector.derivedPK, 'hex');

      expect(cryptoSignVerify(sig, msg, pk)).to.equal(true);
    });
  }
});

describe('cryptoSignSignature', () => {
  it('should throw on invalid sk length', () => {
    const v0 = TEST_VECTORS[0];
    const sk = Buffer.from('00', 'hex');
    const msg = Buffer.from(v0.msg, 'hex');
    const sig = Buffer.alloc(CryptoBytes);

    expect(() => {
      cryptoSignSignature(sig, msg, sk, false);
    }).to.throw('invalid sk length');
  });
});

describe('signingErrors', () => {
  const hashedseed = TEST_VECTORS[0].hashedSeed;
  it('should throw an error if the pk or sk is null', () => {
    const pkBad = null;
    const skBad = null;
    const pk = new Uint8Array(CryptoPublicKeyBytes);
    const sk = new Uint8Array(CryptoSecretKeyBytes);
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pkBad, sk);
    }).to.throw('pk/sk cannot be null');
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pk, skBad);
    }).to.throw('pk/sk cannot be null');
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pkBad, skBad);
    }).to.throw('pk/sk cannot be null');
  });
  it('should throw an error if the pk or sk had an invalid length', () => {
    const pkBad = new Uint8Array(12);
    const skBad = new Uint8Array(12);
    const pk = new Uint8Array(CryptoPublicKeyBytes);
    const sk = new Uint8Array(CryptoSecretKeyBytes);
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pkBad, sk);
    }).to.throw('invalid pk length 12');
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pk, skBad);
    }).to.throw('invalid sk length 12');
    expect(() => {
      cryptoSignKeypair(Buffer.from(hashedseed, 'hex'), pkBad, skBad);
    }).to.throw('invalid pk length 12');
  });
});
